pragma solidity ^0.4.17;

import "./Flow.sol";

/**
 * The Genesis contract is responsible for creating and maintaining an index of 
 * Flow contracts
 */
contract Genesis {
  address private owner;
  string[] public stations;
  mapping (uint => mapping (uint => address[])) private flows;

  function Genesis() public {
    owner = msg.sender;
  }

  modifier onlyBy(address _account) {
    require(msg.sender == _account);
    _;
  }  

  function addStation(string memory stationName) public onlyBy(owner) returns (uint) {
    stations.push(stationName);

    return stations.length - 1;
  }

  function addFlow(
    uint origin, 
    uint destination, 
    Flow.TransportMode mode,
    bool reversible,
    address flowOwner
  ) public onlyBy (owner) {
    address flow = new Flow(mode, reversible, flowOwner);

    flows[origin][destination].push(flow);
  }
}
